/*
 * W.J.P to Calculate Arithmetic Operation
 * */
package demo;

public class operator_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int a=50,b=50;
			
			System.out.println("a" + "+" + "b" + "=" + (a+b));
			System.out.println("a" + "-" + "b" + "=" + (a-b));
			System.out.println("a" + "*" + "b" + "=" + (a*b));
			System.out.println("a" + "/" + "b" + "=" + (a/b));
			
	}

}
